(25, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\"><u>LAYOUTS USING DIVISIONS</u></span></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;Div&gt; helps us in multiple way.it helps in creating divisions. It helps to group multiple element.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We use external CSS file for style statements and divisions properties and then link to the reqired HTML document.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Ex- external CSS file </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp; <strong>Body {</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Max-width: 1000px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Margin: auto;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Font-family: cursive;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Font-size: 16px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Div {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Border: 1px solid black</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Margin-top: 5px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Padding: 20px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;HTML allows us to use float property to decide which side your elements should float.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div id= &ldquo;left&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h2&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/h2&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;ul&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/ul&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div id= &ldquo;right&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h2&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/h2&gt;</span></strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;ul&gt;</span></strong></span></p>\r\n\r\n<p style=\"text-align:right\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/ul&gt;</span></strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We can use id to style any element in our html file.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">CSS file </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Div {border:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Margin:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Padding:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#left {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Float: left;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Padding:0;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Border: none;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#right {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Float: right;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Padding: 0;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Border: none:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<u><strong> Different styling on multiple div elements</strong></u> </span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Left {</span></strong></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Float: left;</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } </span></strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Right {</span></strong></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Float: right;</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class=&rdquo; left&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class=&rdquo; right&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Notes</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; Since Id attribute is used to identify elements uniquely, ideally 2 elements should not have the Same id.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; The commonly used block level elements&nbsp;are:</span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;div&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;footer&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;form&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;h1&gt; to &lt;h6&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;header&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;li&gt; &lt;ol&gt;&lt;ul&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;p&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;nav&gt;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;table&gt;</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <u>Creating a div which will have both des these divs</u></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp;New class in the CSS file. We box class to the named&nbsp;box will assign this divs. The box class will have the following</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">(1) Create a new class in the CSS file. We box class to&nbsp;the named box will assign this divs. The box class will have the following</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">styling</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&bull; box {display: inline-block;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;width: 50%;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; float: left;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">2 Create a new div which will contain both these divs. Assign list-box class to this section. Go to stylesheet and&nbsp;</span><span style=\"font-family:Calibri,sans-serif\">write following styles statement</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">list-box {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">width: 100% </span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">overflow: hidden;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">In HTML document create new div and include both our lists in new&nbsp;</span><span style=\"font-family:Calibri,sans-serif\">div. The Code :</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;div class=&quot;list-box&gt; &lt;div class=&quot;box&quot;&gt;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Include left content for your ordered list.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt; &lt;div class box &gt;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Include your le right content for</span><span style=\"font-family:Calibri,sans-serif\">your unordered list. &lt;/div&gt;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></span></p>\r\n', 'Layouts Using division'),
(26, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">Adding Images</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Images are added using &lt;img&gt;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&lt;Img src=&quot;&quot; title=&quot;&quot; width=&quot;&quot; height=&quot;&quot;&gt;&lt;/img&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">src(Source) of the image. It may be location or a link. </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Title name you want to give to the image.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">width and height are the magnitude of the image.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">alt -&gt;alternative text.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We Can use float properties with</span>&nbsp;i<span style=\"font-family:Calibri,sans-serif\">mages also. for this, we will set id attribute.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">id=&quot;image&quot;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">CSS file</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#image</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">float: left:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#identity {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; overflow: auto:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n', 'Adding Images'),
(27, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><u><span style=\"font-family:Calibri,sans-serif\">Background image</span></u></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div id=&quot;background-image&quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">css file</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#background-image {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">background-image: url(&quot; &quot;);</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">height: </span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">width:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;Color:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">margin:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#Content {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">background Color: rgba (0, 0, 0, 0.7);</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">max-width:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">margin:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\"><u>HTML docs</u></span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;body&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; &lt;div id=&rdquo; background-image&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; &lt;center&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp; &lt;div id=&rdquo; content&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; &lt;h1&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/h1&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp; &lt;h4&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/h4&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/center&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/body&gt;</span></strong></span></p>\r\n', 'Adding a Background image'),
(28, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">Tables</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We use &lt;table&gt; to create a table. The content of table is then added. using &lt;tbody&gt; element. A row within table is created using &amp; &lt;tr&gt; element.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Data in table row is added using &lt;td&gt; element. </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&lt;th&gt; is used for the heading of the table.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;table&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;tboy&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;tr&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;th&gt;&lt;/th&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;th&gt;&lt;/th&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/tr&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;tr&gt;&lt;td&gt; &lt;/td&gt; &lt;td&gt;&lt;/td&gt;&lt;td&gt;&lt;/td&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/tr&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/tbody&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/table&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">CSS file for tables.</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">table {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; border: 1px solid;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; width: 100%;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;border-collapse: Collapse;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">th, td {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding: 15px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">border: 1px solid #04a4a4;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">height: 30px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">th {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">text-align: left &amp; background-color: #ececec</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">background-color: #ececec;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Table'),
(29, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">Navigation</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Navigations are used to link different Sections within the&nbsp;</span><span style=\"font-family:Calibri,sans-serif\">same page.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;Navigation links added using &lt;nav&gt; are &lt;nav&gt; is used in Similar way to a &lt;ul&gt;.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;nav&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;ul&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp; &lt;li&gt;&lt;a href=&quot;id&quot;&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/a&gt;&lt;/li&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp; &lt;li&gt;&lt;a href=&quot;id&quot;&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/a&gt;&lt;/li&gt; </span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp; &lt;li&gt;&lt;a href=&quot;id&quot;&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/a&gt;&lt;/li&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/u1&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/nav&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">link sections along with logo is known header</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">for logo:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div id=&quot;header&quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;a href=&quot;#&quot; class=&quot;logo&quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;img src=&quot; &quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/a&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;nav&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/nav&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;For CSS file</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">nav {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;list-style type: none;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">margin: 0:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding:0;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">a {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp; display: block:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp; Color: #666;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding: 14px 16px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">text-decoration: none;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">float: right;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">logo</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">float: left;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">height: 40px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">#header {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding: 5px 0px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">border-bottom: 1px solid;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Navigation'),
(30, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Forms</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Forms used to collect users input are which can be stored in database or Can be sent to server for processing. No external CSS file is necessary. we use form element <strong>&lt;form&gt; </strong>to&nbsp;create a form. Input elements attribute are used with type</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type=&quot;&quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;This tells about the nature of the&nbsp;inputs</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">placeholder=&quot;&quot;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Hints the user in the input box.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Name=&quot;&quot;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&rarr; Name of the input data.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type=&quot;text&quot; placeholder=&quot;Enter first name&quot; name=&quot;First name&quot;&gt;&lt;br&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Type=&quot;radio&quot;</strong> is used Select predefined inputs. </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>type=&quot;checkbox&quot; </strong>is used to create checkboxes ticks.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Ex- </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;body&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h1&gt;Learning forms in&nbsp;HTML&lt;/h1&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type=&quot; name=&quot;&quot;&gt;&lt;br&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type=&quot;radio&quot; name=&quot;gender&quot; value=&quot;male&quot; &gt; Male &lt;br&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type= &quot;checkbox&quot; name=&quot;drinks&quot; &quot;value=&quot;tea&quot; &gt; Tea&lt;/input&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type=&quot;Submit&quot;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/form&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/body&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\"><u>CSS</u></span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Input {padding: 14px 70px; font-size: 15px margin-bottom: 106x:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;#subscribe { border: none;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">bg-color: black;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Color: white;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">font-size: 166x; margin: 4px - 46x;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">padding: 16px 40px;}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Forms'),
(31, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\"><u>Pseudo classes</u></span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Selector: pseudo-class property: value:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Pseudo classes are useful in tables and lists for background colour.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;style&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">a: link {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Color: red;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;a:visited{</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;Color: green;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">a:hover {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Color: blue;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">a: active {</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Color: yellow</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">li: nth-child (even) {background-color: Yellow;}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;li: nth-child (odd) {background-color: grey;}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/style&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Some of the most used pseudo classes are:-</span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">link, visited, active, hover, focus, first child, nth-child</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">The order of decoration is very important.</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; <strong>&nbsp; &nbsp;link&rarr; visited--&gt; hover --&gt;focus &rarr; active.</strong></span></span></p>\r\n', 'Pseudo classes'),
(32, 'CSS', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\"><u>Meta tags</u></span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; list of Some important meta tags.</span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Title tag: Sels the title of the page, which is displayed on the browser tab.</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;&lt;title&gt; My web Page &lt;/title&gt;</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Meta description: Sets the page descript ion in 3-4 lines. This information is displayed on search results as link description. </span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;&nbsp; &lt;meta type=&quot;description&quot; content=&quot;This is my JustASSK&nbsp;website.&quot;&gt;</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">meta keywords: The keywords are an important part of SEO. These comma(s) seperated words define the Content that exists en the page and is used by search engine to a 9 the Same Search match with query.</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>&lt;meta type &quot;keywords&quot; content=&quot;JustASSK, Webpage, Meta tags&quot;&gt;</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; IV. Meta robot: defines to what extent crawler or a robot can page. access the</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp;<strong> &nbsp; &lt;meta type=&quot;robot&quot; Content = &quot;Selections&quot;&gt;</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; V.Meta-revisit after: defines after what time a Search engine crawler should revisit the page.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; <strong>&nbsp; &nbsp; &nbsp; &lt;meta type=&quot;revisit-after&quot; content=&quot;&nbsp; &quot;&gt;</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; VI. Meta language: defines what language is the page written</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&lt;meta type=&quot;language&quot; content=&quot;English&rdquo;&gt;</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp;VII.Meta author: Defines the author of the page content. Helps in author based search engine queries!</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&lt;meta type=&quot;author&quot; Content=&quot;JustASSK&rdquo;&gt;</strong></span></span></p>\r\n', 'Meta tags'),
(33, 'BOOTSTRAP', '<p style=\"margin-left:72px; text-align:center\"><span style=\"font-size:18px\"><strong><u><span style=\"font-family:Calibri,sans-serif\">PREDEFINED CLASSES</span></u></strong></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">. &lsquo;&lsquo;visible-lg&rsquo;&rsquo;class</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">visible- lg div will only be visible for screen size &gt;=1200px.</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">. &quot;hidden-lg&quot;</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Hidden-lg div only be visible for screen size &lt; 1200px.</span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Bootstrap defined 4 types of devices and 3 breakpoints.</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Large devices &gt;=1200px lg</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Medium devices 992px to 1199px md</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Small devices 768 px to 991px sm</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Extra small devices &lt;768px xs</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Bootstrap classes add predefined CSS properties to HTML element. Use inspect element on the output of the classes to know which are the properties added by the bootstrap class.</span></span></p>\r\n', 'Predefined classes'),
(34, 'BOOTSTRAP', '<p style=\"margin-left:72px; text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">CONTAINER ELEMENTS</span></strong></u></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">All the contents in a web page are included in the container element.</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Bootstrap has 2 container elements</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Container (doesn&rsquo;t include bg-colour)</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Container-fluid</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">The container class leaves some margin on both the sides of the webpage.</span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">768px-&gt;750px</span></strong></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">992px-&gt;970px</span></strong></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">1200px-&gt;1170px</span></strong></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">The container-fluid uses full width of the webpage.</span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">*<strong>We can&rsquo;t use container inside container.</strong></span></span></p>\r\n\r\n<p style=\"margin-left:120px\">&nbsp;</p>\r\n', 'Container elements');
INSERT INTO `courses` (`id`, `course_name`, `description`, `topic_name`) VALUES
(35, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">&nbsp;JUMBOTRON</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">A Jumbotron is a big-box for calling extra attention to special contents and information.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Class jumbotron is used to create a jumbotron.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;body&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;&lt;h3&gt;--&lt;/h3&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;jumbotron&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h1&gt;This is a jumbotron&lt;/h1&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/body&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">All the bootstrap classes are responsive.</span></span></p>\r\n', 'Jumbotron'),
(36, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">GLYPHICON</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We use &lt;span&gt; to include glyphicon in our page.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">You may include glyphicon either in opening or in closing tag.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Depending on the requirement.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h3&gt;search&lt;span class= &lsquo;&lsquo;&lsquo;&rsquo;&gt;&lt;/span&gt;</span></strong></span></p>\r\n', 'Glyphicon'),
(37, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">TABLE</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;table class= &lsquo;&lsquo;table table-striped table-bordered&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/table&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">This will create a table.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;table class= &lsquo;&lsquo;table table-hover&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">It is used to highlight the content when someone hovers over it.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We use &lsquo;table-responsive&rsquo; predefined class to add a scroll-bar at the bottom of the table useful for screen sizes less than <strong>768px</strong>.</span></span></p>\r\n', 'Table'),
(38, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">LAYOUT</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">The ideal way of creatin a web page is to divide it into rows and columns.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Grid-structure:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Rows and columns</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Bootstrap predefined class makes this tedious task easier.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">CREATING LAYOUT</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">The bootstrap grid-structure automatically allows us to divide the available width on a webpage into 12 sections called columns.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Class= &lsquo;&lsquo;col-screen type-number&rsquo;&rsquo;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h3&gt; columns&lt;/h3&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-lg-6 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column1</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-lg-6 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column2</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">If we need different column size for different screen types.</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-md-6 col-lg-2 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column1&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-md-6 col-lg-10 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column2</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We can use multiple column stylings within same div columns.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Rows</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;row&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-lg-6 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column1</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-lg-6 column-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Column2</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri, sans-serif\"><strong>We use Offset for making spaces from either left or right side of the screen width.</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Layouts'),
(39, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">NAVBAR</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;body&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;nav class= &lsquo;&lsquo;navbar navbar-default&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;header&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;a href = &lsquo;&lsquo;#&rsquo;&rsquo;class= &lsquo;&lsquo;navbar-brand&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">JustASSK&lt;/a&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;ul class= &lsquo;&lsquo;nav navbar-nav navbar-right&rdquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;li class= &lsquo;&lsquo;active&rsquo;&rsquo;&gt;&lt;a href = &lsquo;&lsquo;#&rsquo;&rsquo;&gt;link &lt;/a&gt;&lt;/li&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;li&gt;&lt;a href = &lsquo;&lsquo;#&rsquo;&rsquo;&gt;signup &lt;/a&gt;&lt;/li&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;li&gt;&lt;a href = &lsquo;&lsquo;#&rsquo;&rsquo;&gt;link2 &lt;/a&gt;&lt;/li&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/ul&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">This the basic navbar styling.</span></strong></span></p>\r\n', 'Navbar'),
(40, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">IMAGES</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. img-responsive</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">{</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Display: block;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Max-width:100%</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Height:auto;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Html</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;row&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-xs-6&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h3&gt;image&lt;/h3&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;img src= &lsquo;&rsquo;&rsquo;&rsquo; class= &lsquo;&lsquo;img-responsive img-thumbnail&rsquo;&rsquo; alt= &lsquo;&lsquo;responsive img&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">If both img-circle and img-rounded is assigned to one image then img-circle class border will override img-rounded class.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Border-radius img-circle class-&gt;50%</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Border-radius img-rounded-&gt;6px</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">THUMBNAILS</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. thumbnail{</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Display: block;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Padding:4px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Margin-bottom:0px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Line-height:1.42857143;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Background-colour: #fff;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Border: 1px solid #ddd;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Border-radius: 4px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">- webkit - transition: border .2s ease-in-out;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">-o-transition: border .2s ease-in-out;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Transition: border .2s ease-in-out;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;row&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-sm-4&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;thumbnail&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;img src = &lsquo;&rsquo;&rsquo;&rsquo; alt= &lsquo;&rsquo;&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;caption&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h3&gt;&lt;/h3&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;p&gt;&lt;/p&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n', 'Images and thumbnails'),
(41, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">FORMS</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. form-group</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">{</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">Margin-bottom: 15px;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">}</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;row&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-xs-4&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h1&gt;heading&lt;/h1&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;form&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;form-group&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;label for= &lsquo;&lsquo;first name&rsquo;&rsquo;&gt;First name&lt;/label&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type= &lsquo;&lsquo;&rsquo;&rsquo; class= &lsquo;&lsquo;form-control&rsquo;&rsquo; name= &lsquo;&lsquo;first name&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;form-group&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;label for= &lsquo;&lsquo;last name&rsquo;&rsquo;&gt;Last name&lt;/label&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type= &lsquo;&lsquo;&rsquo;&rsquo; class= &lsquo;&lsquo;form-control&rsquo;&rsquo; name= &lsquo;&lsquo;last name&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/form&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We can use &lsquo;input-lg&rsquo; to increase the size of input box.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">Inline form</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-xs-12&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;form class= &lsquo;&lsquo;form-inline&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Checkboxes and radio buttons</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;checkbox&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">I want:</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;label&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/label&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">The label tags add padding -left:20px and margin-bottom:20px.</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;radio&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;label&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input&gt;&lt;/label&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;select class= &lsquo;&lsquo;form-control&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;option&gt;1&lt;/option&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;option&gt;2&lt;/option&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;option&gt;3&lt;/option&gt;</span></strong></span></p>\r\n', 'Forms'),
(42, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">Button styling</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;input type= &lsquo;&lsquo;submit&rsquo;&rsquo; value= &lsquo;&lsquo;submit&rsquo;&rsquo; class= &lsquo;&lsquo;btn btn-default&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">This is the default button styling.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">There are 5 others button styling.</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-primary</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-success</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-info</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-warning</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-danger</span></strong></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">We can also change the size of the button by pre-defined classes.</span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-lg</span></strong></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-md</span></strong></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-sm</span></strong></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. btn-xs</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;button type= &lsquo;&lsquo;submit&rsquo;&rsquo; class= &lsquo;&lsquo;btn btn-primary btn-block&rsquo;&rsquo; &gt;submit&lt;/button&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; *btn-block is used to take the entire space for button.</span></p>\r\n', 'Button styling'),
(43, 'BOOTSTRAP', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong><span style=\"font-family:Calibri,sans-serif\">PANEL STYLING</span></strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;container&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;row row-style&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;col-xs-6&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;panel panel-default&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;panel-heading&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;h4&gt;JustASSK&lt;/h4&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;panel-body&rsquo;&rsquo;&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;p&gt;Basic panel&lt;/p&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;button class= &lsquo;&lsquo;btn btn-primary&rsquo;&rsquo;&gt;Demo&lt;/button&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;div class= &lsquo;&lsquo;panel-footer&rsquo;&rsquo;&gt;Panel footer&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">&lt;/div&gt;&lt;/div&gt;</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">It is not compulsory to add panel heading and panel-footer.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">There are 5 panel-styling</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. panel-primary</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. panel-success</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. panel-info</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. panel-warning</span></strong></span></li>\r\n	<li><span style=\"font-size:18px\"><strong><span style=\"font-family:Calibri,sans-serif\">. panel-danger</span></strong></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">External CSS files are linked after bootstrap CSS bootstrap class.</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Padding inside panel is provided by panel-body, panel-heading or panel-footer classes.</span></span></p>\r\n\r\n<p style=\"margin-left:24px\">&nbsp;</p>\r\n', 'Panel styling'),
(44, 'PHP', '<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><strong><u>Function</u></strong></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">A function is an independent code that performs a particular task. It takes input, perform some actions and then gives the output.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Function starts with the keyword &#39;function&#39;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php Arguments&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>function fn_name ($parameter 1, $parameter 2)</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>{</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; $operation = $parameter 1 *$parameter2:&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; return $operation;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>}</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;html&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;&lt;head&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp;&lt;title&gt; functions &lt;/title&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;&lt;/head&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;&lt;body&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$var1 =18;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$var2= 12;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$sum = sum ($var1, $var2);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Echo &quot;Sum of 2 variables, is&quot; .$sum;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &lt;?&gt;&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;&lt;/body&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;/html&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">It is not necessary that function will always return something. It may or may not retum.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Function'),
(45, 'PHP', '<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong>Arrays</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">An array is a special variable, which can hold more than one value at a time.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">we can assign 2 different values to a single variable using arrays.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;html&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &lt;head&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;title&gt; Arrays&lt;/title&gt;&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;/head&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;body&gt;&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$numbers = array (18,12);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$sum = $numbers [0] + $mumbers [1];</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo &quot;Sum of 2 variables is &quot;. $sum;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo&quot; length of the array is &quot;.Size of ($number}</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;/body&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;/html&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&rArr;we can also use &#39;count&#39; datatype instead of &#39;size of&#39;.</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>If we change the indices of arrays according to our needs, it is known as associative arrays.</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>Snumbers = array (&quot;first_number&quot;=&gt; 18, &nbsp;&quot;second_number&quot; =&gt; 12);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$Sum= $numbers [&quot;first_number&quot;] + $numbers[ &quot;second_number&quot;];</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo &quot; sum of 2 variables is &quot;$sum.&quot;.&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n', 'Arrays'),
(46, 'PHP', '<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong>Strings</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">A string is a set of characters that can contain spaces and numbers.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><u><strong>Single quotes</strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\">double quotes inside single quotes will be Considered as strings.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Variables inside single &nbsp;quotes will be considered as strings and will display the variable name only.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ex-&nbsp; &nbsp;<strong>$variable = 5;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; echo &#39;The output is $variable&#39;;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &rarr;The output is $variable.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><u><strong>Double quotes</strong></u></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Single quotes inside double quotes will be considered as strings. Variables inside double quotes will be Considered as variables only and will display the value of the variables instead of name.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ex-&nbsp; <strong>&nbsp;$variable=5;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; echo&quot; The output is $variable&quot;;&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; The outbut is 5.</span></p>\r\n', 'Strings'),
(47, 'PHP', '<h3><span style=\"font-size:18px\"><u><strong>If else condition</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$marks =50;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>if ($marks &gt;= 40)</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>{</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo &quot;The student has passed&quot;; }</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>else {</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo &quot;The student has failed&quot;; }</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:18px\"><u><strong>While loop</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">We use while look if we want to run same block of code again and again.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$counter 1;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>while ($counter &lt;=5);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;{ echo $counter. &quot;&lt;br&gt;&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;$counter++;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>}</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:18px\"><u><strong>For and foreach</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp;for($counter 1; $counter &lt;=5; $counter)</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;{</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; &nbsp; echo $counter. &quot;&lt;br/&gt;&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;}</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>For each loop is used to loop through arrays.</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$marks = array ( 4, 8, 15, 16, 23, 42);&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>foreach ($marks as $mark)</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; {</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo $mark. &quot; , &quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp; }</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Conditionals and loops'),
(48, 'PHP', '<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong>Database Connection</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">In php, we use mysqli_connect fn to establish the connection between web pages and databases.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ex -&nbsp;<strong> mysqli_connect (&quot;host&quot;, &quot;usemame&quot;, &quot;password&quot;, &quot;databases_name&quot;);</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&rarr;&#39;mysqli_error()&#39; returns the error message for the most recent mysqli function call.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$con = mysqli_connect(&quot;localhost&quot;, &quot;root&quot;, &quot; &nbsp;&quot;, &quot;database_name&quot;) or die (mysqli_error &#39;($con));</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\">This $con variable will help us use the connection and Communicate with the database.</span></li>\r\n	<li><span style=\"font-size:18px\">die() &nbsp;is used to stop the execution of code here if database connection is not established.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'Database connection'),
(49, 'PHP', '<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><u><strong>Select query in PHP</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$con= mysqli_connect (&quot;localhost&quot;, &quot;root&quot;, &quot; &quot;, &quot;database_name&quot;) or die (mysqli-error($con));</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$select_query = &quot;SELECT id, email, first_name FROM users&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$select_query_result = mysql_query ($con, $select_query) or die(mysqli_error($con));</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$total_rows_fetched = mysqli_num_rows</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>($select_query result);&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo $total rows fetched;&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">(i) We will first Connect the database.<br />\r\n(ii)Select query is a variable that contains sql query as string.<br />\r\n(iii) To run the function, we use (mysqli_query().</span></p>\r\n\r\n<p><span style=\"font-size:18px\">mysqli_error() ensures that select query executed successfully.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><u><strong>mysqli-query function:</strong></u></span></h3>\r\n\r\n<p><span style=\"font-size:18px\">This function is used to run SQL queries in PHP. The function takes 2 parameters.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">1- Connection variable.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">2. Variable in which SQL query is stored.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$row= mysqli_fetch_array($select_query_result);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&rarr;&nbsp;fetch fn will return the first row and will be stored in row var.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>&nbsp;<em>echo $row [id],&quot;&lt;br/&gt;&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;echo $row [email]. &quot;&lt;br/&gt;&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;echo $row[&#39;first_name&quot;]. &quot;&lt;br/&gt;&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">&rArr;This will return the data of the first row.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">The form tag contains 2 attribute</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(i) action&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ii) method (get or post)</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php echo $_GET[&#39;email&#39;]; ?&gt;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">To hide the form details in URL, we use POST method. It appends form data inside the body of the http request. It has no limitations and Cannot be bookmarked.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Action attribute consists the name of the file to which the form data will be sent. This attribute &nbsp;may or may not contain any value. If no value is mentioned or this attribute is not mentioned in the form tag, the data will be sent to this page only.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&lt;?php</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>//connection established</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$con = mysqli_connect (&quot;localhost&quot;, &quot;root, &quot; &quot;, &quot;database_name&quot;) or die (mysqli_error($con)</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>//store from values into variables</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$email = $_POST[&#39;email&#39;];</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$first_name = $_POST[&#39;first_name&#39;];&nbsp;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$last name=$_POST[last_name];</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>&nbsp;$phones= $_POST [&#39;phone&#39;];</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>//store insert query in a variable. Use double quotes to let PHP treat variables as variables only.</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$user_reg_query = &quot;insert into users (email, first name, last-name, phone) values ($email, &#39;$first name&#39;, &#39;$last name&#39;, &#39;$phone&#39;);</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>//Execute the Query&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>$user_reg_submit= mysqli_query($con, $user_reg_query ) or die (mysqli_error($con));</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>// If this echo is executed, this means query succesfully executed. Otherwise die fn with mysqli_query fn would have stopped the execution of the Code.</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>echo &quot;User Successfully inserted&quot;;</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><em>?&gt;</em></strong></span></p>\r\n', 'Select query');
